function indices = reassignPoints(X, K, centroids)    m = size(X,1);    indices = zeros(m, 1);    for i=1:m        k = 1;        minimum_distance = sum((X(i,:) - centroids(1,:)) .^ 2);        for j=2:K            distance = sum((X(i,:) - centroids(j,:)) .^ 2);            if(distance < minimum_distance)                minimum_distance = distance;                k = j;            end        end        indices(i) = k;    endend